mapper.insert:


    SELECT LAST_INSERT_ID() as id;

insert into
    employee
values
    (#{id}, #{name}, #{gender}, #{age}, #{time}, #{address}, #{telephone}, #{email}, #{dept_id});

mapper.update:
update
    employee
set
    name = #{name}, address = #{address}, telephone = #{telephone}, email = #{email}
where
      id = #{id};

mapper.select:
select
       *
from
     employee
where
    name like #{searchString}
   or gender like #{searchString}
   or age like #{searchString}
   or address like #{searchString}
   or telephone like #{searchString}
   or email like #{searchString};

mapper.update:
update
    course
set
    course_name = #{course_name}, instructor_id = #{instructor_id}, type = #{type}, content = #{content}
where
    course_id = #{course_id};

mapper.select:
select
    *
from
    course
where
    course_name like #{searchString}
   or instructor_id like #{searchString}
   or type like #{searchString}
   or content like #{searchString}
   or state like #{searchString};

select
    *
from
    log
where
    a_id like #{searchString}
   or operation like #{searchString}
   or date like #{searchString};

mapper.insert:

    SELECT LAST_INSERT_ID() as log_id;

insert into
    log
values
    (#{log_id}, #{a_id}, #{operation}, #{date});
